mapper.select:
SELECT * FROM ( SELECT ROWNUM AS RNUM , B.* FROM ( SELECT U.USED_NO usedNo , U.USED_TITLE usedTitle , U.USED_PRICE usedPrice , U.STATUS status , U.ENROLL_DATE enrollDate , U.VIEW_COUNT viewCount , C.MANUFACTURER manufacturer , C.MODEL model , C.CAR_YEAR carYear , C.DISTANCE distance , C.TRANSMISSION transmission , C.FUEL_TYPE fuelType , C.REGION region , C.CAR_COLOR carColor , C.PHONE phone , CAT.CATEGORY_NAME categoryName , ATT.FILE_PATH thumbnail FROM CT_USED_LISTING U JOIN CT_CAR_INFO C ON U.USED_NO = C.USED_NO JOIN CT_USED_CATEGORY CAT ON U.CATEGORY = CAT.CATEGORY_NO LEFT JOIN ( SELECT REF_BNO , FILE_PATH FROM CT_USED_ATTACHMENT WHERE STATUS = 'Y' AND ROWNUM = 1 ) ATT ON ATT.REF_BNO = U.USED_NO
WHERE U.USED_TITLE LIKE '%' || #{keyword} || '%'
ORDER BY U.USED_NO DESC ) B ) WHERE RNUM BETWEEN #{startRow} AND #{endRow}

mapper.insert:
INSERT
  INTO
       CT_USED_LISTING
VALUES
	   (
	   #{usedNo}
     , #{userNo}
     , #{usedTitle}
     , #{usedContent}
     , #{usedPrice}
     , #{category}
     , '판매중'
     , SYSDATE
     , DEFAULT
     , #{thumbnail}
       )

INSERT
  INTO
       CT_USED_ATTACHMENT
VALUES
       (
       SEQ_USED_FILE_NO.NEXTVAL
     , #{refBno}
     , #{originName}
     , #{changeName}
     , #{filePath}
     , SYSDATE
     , 'Y'
       )
